import React, { useState, useEffect } from "react";
import axios from "axios";
import { useLocation } from 'react-router-dom';

function Home() {
    const location = useLocation();
    const [userData, setUserData] = useState(null);
    const [error, setError] = useState(null);

    useEffect(() => {
        async function fetchUserData() {
            try {
                const response = await axios.get(`http://localhost:8000/user/${location.state.id}`);
                setUserData(response.data);
            } catch (error) {
                console.error("Error fetching user data:", error);
                setError(error.message); // Set the error message
            }
        }

        fetchUserData();
    }, [location.state.id]);

    return (
        <div>
            <h2>Welcome to the Home Page</h2>
            {error ? ( // If there's an error, display the error message
                <p>Error: {error}</p>
            ) : (
                userData ? ( // If userData exists, display user information
                    <div>
                        <p>Username: {userData.username}</p>
                        <p>Email: {userData.email}</p>
                        {/* Render other user data fields here */}
                    </div>
                ) : (
                    <p>Loading...</p> // Otherwise, display a loading message
                )
            )}
        </div>
    );
}

export default Home;
