import React, { useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faEnvelope, faPhone, faLock } from '@fortawesome/free-solid-svg-icons';
import './Signup.css';

function Signup() {
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [phone, setPhone] = useState('');
    const [password, setPassword] = useState('');
    const [networkDevicesname, setNetworkDevicesname] = useState('');
    const [connectedDevices, setConnectedDevices] = useState('');
    const [currentBalance, setCurrentBalance] = useState('');
    const [Amount, setAmount] = useState('');
    const [paymentStatus, setPaymentStatus] = useState('');
    const [networkSpeed, setNetworkSpeed] = useState('');
    const [RemainingDays, setRemainingDays] = useState('');
    const [subscriptionStartDate, setSubscriptionStartDate] = useState('');
    const [signalStrength, setSignalStrength] = useState('');

    async function submit(e) {
        e.preventDefault();

        try {
            await axios.post("http://localhost:8000/signup", {
                username, email, phone, password,networkDevicesname,connectedDevices,currentBalance,Amount,paymentStatus,networkSpeed,RemainingDays,subscriptionStartDate,signalStrength
            })
            .then(res => {
                if (res.data === "exist") {
                    alert("User already exists");
                } else if (res.data === "notexist") {
                    alert("You have registered successfully!");
                }
            })
            .catch(e => {
                alert("Wrong details");
                console.log(e);
            });
        } catch (error) {
            console.log(error);
        }
    }

    return (
        <div className="login">
            <div className="logo">
            <img src="https://i.postimg.cc/CLdpsq5h/Wifi-provider-removebg-preview.png" alt="Wifi-provider"></img>
            </div>
            <h1>Signup</h1>
            <form onSubmit={submit}>
                <div className="input-group">
                <FontAwesomeIcon icon={faUser} className="icon" /><input type="text" value={username} onChange={(e) => setUsername(e.target.value)} placeholder="Username" />
                </div>
                <div className="input-group">
                <FontAwesomeIcon icon={faEnvelope} className="icon" /><input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" />
                </div>
                <div className="input-group">
                <FontAwesomeIcon icon={faPhone} className="icon" /><input type="tel" value={phone} onChange={(e) => setPhone(e.target.value)} placeholder="Phone" />
                </div>
                <div className="input-group">
                <FontAwesomeIcon icon={faLock} className="icon" /><input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" />
                </div>
                <input type="submit" value="Register" />
            </form>
            <p>I have an Account !</p><Link to="/">Login</Link>
        </div>
    );
}

export default Signup;
