import React, { useState } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEnvelope, faLock } from '@fortawesome/free-solid-svg-icons';
import './Login.css';

function Login() {
    const history = useNavigate();

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [username, setUsername] = useState('');

    async function submit(e) {
        e.preventDefault();
        try {
            const response = await axios.post("http://localhost:8000/", {
                email,
                password,
                username,
                // Other data...
            });

            if (response.data === "exist") {
                const idToPass = username || email; // Choose either username or email based on availability
                history("/home", { 
                    state: { 
                        id: idToPass,
                        username: username,
                        // Other fields...
                    } 
                });
            } else if (response.data === "notexist") {
                alert("User has not signed up");
            }
        } catch (error) {
            alert("Wrong details");
            console.error(error);
        }
    }

    return (
        <div className="login">
            <div className="logo">
                <img src="https://i.postimg.cc/CLdpsq5h/Wifi-provider-removebg-preview.png" alt="Wifi-provider"></img>
            </div>
            <h1>Login </h1>
            <form action="POST">
                <div className="input-group">
                    <FontAwesomeIcon icon={faEnvelope} className="icon" /><input type="email" onChange={(e) => { setEmail(e.target.value) }} placeholder="Email" />
                </div>
                <br />
                <div className="input-group">
                    <FontAwesomeIcon icon={faLock} className="icon" /><input type="password" onChange={(e) => { setPassword(e.target.value) }} placeholder="Password" />
                </div>
                <br />
                <input type="submit" onClick={submit} />
            </form>
            <br />
            <p>Create an Account !</p> <Link to="/signup">Register</Link>
        </div>
    );
}

export default Login;
