// Login.js
import React, { useState } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";

function Login() {
    const history = useNavigate();

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [userData, setUserData] = useState(null); // State to store user data

    async function submit(e) {
        e.preventDefault();
        try {
            const response = await axios.post("http://localhost:8000/", {
                email,
                password
            });

            if (response.data === "exist") {
                // Fetch additional user data based on email
                const userDataResponse = await axios.get(`http://localhost:8000/userdata/${email}`);
                if (userDataResponse.data) {
                    setUserData(userDataResponse.data);
                    history("/home", { 
                        state: { 
                            id: email,
                            userData: userDataResponse.data // Pass user data to home page
                        } 
                    });
                } else {
                    alert("User data not found");
                }
            } else if (response.data === "notexist") {
                alert("User has not signed up");
            }
        } catch (error) {
            alert("Wrong details");
            console.error(error);
        }
    }

    return (
        <div className="login">
            <h1>Login</h1>
            <form onSubmit={submit}>
                <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" required />
                <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" required />
                <button type="submit">Login</button>
            </form>
            <p>Create an Account !</p> <Link to="/signup">Register</Link>
        </div>
    );
}

export default Login;
