import React, { useState } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEnvelope, faLock } from '@fortawesome/free-solid-svg-icons';
import './Login.css';

function Login() {
    const history = useNavigate();

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    async function submit(e) {
        e.preventDefault();
        try {
            const response = await axios.post("http://localhost:8000/", {
                email,
                password
            });

            if (response.data === "exist") {
                // Redirect to home page with user data
                history("/home", {
                    state: {
                        id: email,
                        // Additional user data to be passed
                        // You may fetch this data from the server as needed
                        // For now, they are set to empty strings
                        username: '',
                        networkDevicesname: '',
                        connectedDevices: '',
                        currentBalance: '',
                        Amount: '',
                        paymentStatus: '',
                        networkSpeed: '',
                        remainingDay: '',
                        subscriptionStartDate: '',
                        signalStrength: ''
                    }
                });
            } else if (response.data === "notexist") {
                alert("User has not signed up");
            }
        } catch (error) {
            alert("Wrong details");
            console.error(error);
        }
    }

    return (
        <div className="login">
            <div className="logo">
                <img src="https://i.postimg.cc/CLdpsq5h/Wifi-provider-removebg-preview.png" alt="Wifi-provider" />
            </div>

            <h1>Login</h1>
            <form onSubmit={submit}>
                <div className="input-group">
                    <FontAwesomeIcon icon={faEnvelope} className="icon" />
                    <input type="email" onChange={(e) => setEmail(e.target.value)} placeholder="Email" />
                </div>
                <br />
                <div className="input-group">
                    <FontAwesomeIcon icon={faLock} className="icon" />
                    <input type="password" onChange={(e) => setPassword(e.target.value)} placeholder="Password" />
                </div>
                <br />
                <input type="submit" value="Login" />
            </form>
            <br />
            <p>Create an Account !</p>
            <Link to="/signup">Register</Link>
        </div>
    );
}

export default Login;
