import React, { useState, useEffect } from "react";
import axios from "axios";
import { useLocation } from 'react-router-dom';

function Home() {
    const location = useLocation();
    const [userData, setUserData] = useState(null);

    useEffect(() => {
        async function fetchUserData() {
            try {
                const response = await axios.get(`http://localhost:8000/user/${location.state.id}`);
                setUserData(response.data);
            } catch (error) {
                console.error("Error fetching user data:", error);
            }
        }

        fetchUserData();
    }, [location.state.id]);

    return (
        <div>
            <h2>Welcome to the Home Page</h2>
            {userData ? (
                <div>
                    <p>Username: {userData.username}</p>
                    <p>Email: {userData.email}</p>
                    {/* Check if networkDevicesname exists before rendering */}
                    {userData.networkDevicesname && <p>NetworkDevicesname: {userData.networkDevicesname}</p>}
                    {/* Check if paymentStatus exists before rendering */}
                    {userData.paymentStatus && <p>PaymentStatus: {userData.paymentStatus}</p>}
                    {/* Check if connectedDevices exists before rendering */}
                    {userData.connectedDevices && <p>ConnectedDevices: {userData.connectedDevices}</p>}
                    {/* Check if connectionStatus exists before rendering */}
                    {userData.connectionStatus && <p>ConnectionStatus: {userData.connectionStatus}</p>}
                    {/* Check if networkSpeed exists before rendering */}
                    {userData.networkSpeed && <p>NetworkSpeed: {userData.networkSpeed}</p>}
                    {/* Check if signalStrength exists before rendering */}
                    {userData.signalStrength && <p>SignalStrength: {userData.signalStrength}</p>}
                    {/* Check if Amount exists before rendering */}
                    {userData.Amount && <p>Amount: {userData.Amount}</p>}
                    {/* Check if currentBalance exists before rendering */}
                    {userData.currentBalance && <p>CurrentBalance: {userData.currentBalance}</p>}
                    {/* Check if subscriptionStartDate exists before rendering */}
                    {userData.subscriptionStartDate && <p>SubscriptionStartDate: {userData.subscriptionStartDate}</p>}
                    {/* Check if RemainingDays exists before rendering */}
                    {userData.RemainingDays && <p>RemainingDays: {userData.RemainingDays}</p>}
                </div>
            ) : (
                <p>Loading...</p>
            )}
        </div>
    );
}

export default Home;
