import React, { useState } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";

function Signup() {
    const history = useNavigate();

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [username, setUsername] = useState(''); // New state for username
    const [phoneNumber, setPhoneNumber] = useState(''); // New state for phone number

    async function submit(e) {
        e.preventDefault();

        try {
            const res = await axios.post("http://localhost:8000/signup", {
                email, password, username, phoneNumber // Include username and phone number in the request
            });

            if (res.data === "exist") {
                alert("User already exists");
            } else if (res.data === "notexist") {
                history("/home", { state: { id: email } });
            }
        } catch (error) {
            alert("Error: " + error.message);
            console.log(error);
        }
    }

    return (
        <div className="signup">
            <h1>Signup</h1>
            <form action="POST">
                <input type="email" onChange={(e) => { setEmail(e.target.value) }} placeholder="Email" />
                <input type="password" onChange={(e) => { setPassword(e.target.value) }} placeholder="Password" />
                <input type="text" onChange={(e) => { setUsername(e.target.value) }} placeholder="Username" /> {/* Add input field for username */}
                <input type="tel" onChange={(e) => { setPhoneNumber(e.target.value) }} placeholder="Phone Number" /> {/* Add input field for phone number */}
                <input type="submit" onClick={submit} />
            </form>
            <br />
            <p>OR</p>
            <br />
            <Link to="/">Login Page</Link>
        </div>
    );
}

export default Signup;
