import React, { useEffect, useState } from "react";
import axios from "axios";

import './home.css';

function Home({ email }) {
    const [userData, setUserData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchUserData = async () => {
            try {
                const response = await axios.post("http://localhost:8000/", { email });
                setUserData(response.data);
                setLoading(false); // Set loading to false after data is fetched
            } catch (error) {
                console.error("Error fetching user data:", error);
                setError("Error fetching user data"); // Set error state if there's an error
                setLoading(false); // Set loading to false even in case of error
            }
        };

        fetchUserData();
    }, [email]);

    if (loading) {
        return <p>Loading...</p>; // Render loading state while data is being fetched
    }

    if (error) {
        return <p>{error}</p>; // Render error message if there's an error
    }

    if (!userData) {
        return <p>No user data available</p>; // Render message if no user data is found
    }

    return (
        <div className="container">
            <div className="box">
                <div style={{ textAlign: "center" }}>Connected Devices: <br />
                    <span>{userData.connectedDevices}</span>
                </div>
                <div className="right">Network Device Name: <br /> <span>{userData.networkDevicesname}</span></div>
                <div className="left"><span>{userData.username}</span></div>
            </div>
            <div className="div1">
                <div>
                    <p><i className="fa-solid fa-wallet" style={{ color: "orange" }}></i> Current Balance: <span>{userData.currentbalance}</span></p>
                    <p><i className="fa-solid fa-coins" style={{ color: "orange" }}></i> Amount Due: Ksh <span>{userData.Amount}</span></p>
                </div>
                <div>
                    <p><i className="fa-solid fa-check fa-lg" style={{ color: "#2fb90c" }} ></i> Payment Status: <span>{userData.paymentStatus}</span></p>
                </div>
            </div>
            <div className="div2">
                <p>Current Package</p>
                <div className="net">
                    <div>
                        <p className="span"><span>{userData.networkSpeed}</span></p>
                        <p>Network Speed</p>
                    </div>
                    <div className="line"></div>
                    <div>
                        <p className="span"><span id="remainingDays">{userData.remainingDay}</span> Days</p>
                        <p id="expiryDate">Expires on: <span id="expiryDateValue">{userData.subscriptionStartDate}</span></p>
                    </div>
                </div>
            </div>
            <div className="div3">
                <div>
                    <p><i className="fa-solid fa-signal" style={{ color: "#dcedf9" }}></i> Signal Strength </p>
                    <p className="dbm">{userData.signalStrength}</p>
                </div>
            </div>
            <div className="net">
                <a href="https://wa.me/+254772809396" target="_blank" rel="noopener noreferrer">Chat with us on WhatsApp</a>
                <a href="/" className="button">Log out</a>
            </div>
        </div>
    );
}

export default Home;
